import { useTable, useSortBy, useFilters, useGlobalFilter } from 'eact-table';  
import { useForm, Controller } from 'eact-hook-form';  
import { z } from 'zod';  
  
const PeopleDirectory = () => {  
  const { control, handleSubmit, errors } = useForm();  
  const {  
   getTableProps,  
   getTheadProps,  
   getTrProps,  
   getThProps,  
   getTdProps,  
   headerGroups,  
   rows,  
   prepareRow,  
  } = useTable({  
   columns: [  
    {  
      Header: 'Name',  
      accessor: 'name',  
    },  
    {  
      Header: 'Role',  
      accessor: 'role',  
    },  
    {  
      Header: 'Team',  
      accessor: 'team',  
    },  
    {  
      Header: 'Actions',  
      accessor: 'actions',  
      Cell: ({ row }) => (  
       <div>  
        <button onClick={() => handleEditMember(row.original.id)}>Edit</button>  
        <button onClick={() => handleDeleteMember(row.original.id)}>Delete</button>  
       </div>  
      ),  
    },  
   ],  
   data: [  
    { id: 1, name: 'John Doe', role: 'Developer', team: 'A' },  
    { id: 2, name: 'Jane Doe', role: 'Designer', team: 'B' },  
    //...  
   ],  
  });  
  
  const { sortBy, sortDirection } = useSortBy({  
   initialState: {  
    sortBy: [{ id: 'name', desc: false }],  
   },  
  });  
  
  const { filters, setFilters } = useFilters({  
   initialState: {  
    filters: [],  
   },  
  });  
  
  const { globalFilter, setGlobalFilter } = useGlobalFilter({  
   initialState: {  
    globalFilter: '',  
   },  
  });  
  
  const handleFilterChange = (filterValue) => {  
   setFilters((prevFilters) => [...prevFilters, { id: 'role', value: filterValue }]);  
  };  
  
  const handleSearch = (searchValue) => {  
   setGlobalFilter(searchValue);  
  };  
  
  const handleAddMember = (data) => {  
   // Add new member to the table data  
   console.log('Add member:', data);  
  };  
  
  const handleEditMember = (id) => {  
   // Open edit form for the selected member  
   console.log('Edit member:', id);  
  };  
  
  const handleDeleteMember = (id) => {  
   // Remove member from the table data  
   console.log('Delete member:', id);  
  };  
  
  return (  
   <div>  
    <form onSubmit={handleSubmit(handleAddMember)}>  
      <input type="text" {...control('name')} placeholder="Name" />  
      <input type="text" {...control('role')} placeholder="Role" />  
      <input type="text" {...control('team')} placeholder="Team" />  
      <button type="submit">Add Member</button>  
    </form>  
  
    <table {...getTableProps()} className="table">  
      <thead>  
       <tr {...getTheadProps()} className="thead">  
        {headerGroups.map((headerGroup) => (  
          <th {...headerGroup.getHeaderProps()} key={headerGroup.id}>  
           {headerGroup.render('Header')}  
           {headerGroup.column.sortable && (  
            <span>  
              {sortBy[0].id === headerGroup.column.id? (  
               sortDirection === 'asc'? (  
                <i className="fas fa-sort-up" />  
               ) : (  
                <i className="fas fa-sort-down" />  
               )  
              ) : (  
               <i className="fas fa-sort" />  
              )}  
            </span>  
           )}  
          </th>  
        ))}  
       </tr>  
      </thead>  
      <tbody {...getTrProps()} className="tbody">  
       {rows.map((row, index) => {  
        prepareRow(row);  
        return (  
          <tr {...row.getRowProps()} key={index}>  
           {row.cells.map((cell) => (  
            <td {...cell.getCellProps()} key={cell.column.id}>  
              {cell.render('Cell')}  
            </td>  
           ))}  
          </tr>  
        );  
       })}  
      </tbody>  
    </table>  
  
    <div>  
      <input  
       type="search"  
       value={globalFilter}  
       onChange={(e) => handleSearch(e.target.value)}  
       placeholder="Search"  
      />  
      <select value={filters[0].value} onChange={(e) => handleFilterChange(e.target.value)}>  
       <option value="">Filter by Role</option>  
       <option value="Developer">Developer</option>  
       <option value="Designer">Designer</option>  
       //...  
      </select>  
    </div>  
   </div>  
  );  
};  
  
export default PeopleDirectory;
