import React, { useMemo, useState, useCallback } from 'react';
import {
  useReactTable,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  flexRender
} from '@tanstack/react-table';
import { PencilSquareIcon, TrashIcon, FunnelIcon } from '@heroicons/react/24/outline';
import '../styles.css'; // Include your custom styles

// Assuming you have a placeholder image URL
const placeholderImage = 'https://example.com/placeholder.jpg';

const defaultData = [
  { id: 1, name: 'John Doe', age: 28, role: 'Frontend Developer', team: 'Design', photo: placeholderImage, email: 'johndoe@example.com', dob: '01-01-1996', gender: 'Male', nationality: 'American', contact: '1234567890', workEmail: 'johndoe@company.com' },
  { id: 2, name: 'Jane Smith', age: 34, role: 'Backend Developer', team: 'Product', photo: placeholderImage, email: 'janesmith@example.com', dob: '10-10-1989', gender: 'Female', nationality: 'Canadian', contact: '0987654321', workEmail: 'janesmith@company.com' },
  // Add more sample data as needed
];

function PeopleDirectory() {
  const [data, setData] = useState(defaultData);
  const [selectedMember, setSelectedMember] = useState(null);

  const handleDelete = useCallback((id) => {
    setData((prevData) => prevData.filter((item) => item.id !== id));
  }, []);

  const handleMemberClick = (member) => {
    setSelectedMember(member);
  };
  const handleEdit = (id) => {
    // Logic to handle edit action
    console.log(`Editing person with id: ${id}`);
    // Redirect to edit page or open a form/modal
  };

  const closePopup = () => {
    setSelectedMember(null);
  };

  const columns = useMemo(
    () => [
      {
        accessorKey: 'photo',
        header: 'Photo',
        cell: ({ row }) => <img src={row.original.photo} className="member-photo" alt="Member" />,
      },
      {
        accessorKey: 'name',
        header: 'Name',
        cell: ({ row }) => (
          <span className="member-name" onClick={() => handleMemberClick(row.original)}>
            {row.original.name}
          </span>
        ),
      },
      {
        accessorKey: 'email',
        header: 'Email',
        cell: ({ row }) => <span className="member-info member-email">{row.original.email}</span>,
      },
      {
        accessorKey: 'role',
        header: 'Role',
        cell: ({ row }) => <span className="member-info member-role">{row.original.role}</span>,
      },
      {
        accessorKey: 'team',
        header: 'Team',
        cell: ({ row }) => <span className="member-info member-team">{row.original.team}</span>,
      },
      {
        id: 'actions',
        header: 'Actions',
        cell: ({ row }) => (
          <div className="member-actions">
            <button className="member-action-button" onClick={() => handleEdit(row.original)}>
              <PencilSquareIcon className="icon-edit" />
            </button>
            <button
              className="member-action-button text-red-500 hover:bg-red-600 hover:text-white"
              onClick={() => handleDelete(row.original.id)}
            >
              <TrashIcon className="icon-delete" />
            </button>
          </div>
        ),
      },
    ],
    [handleDelete]
  );

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
  });

  return (
    <div className="people-directory">
      <div className="header">
        <div className="team-members-count">
          Team members <span className="user-count">{data.length} users</span>
        </div>
        <div className="header-actions">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search..."
              className="search-bar search-input"
              onChange={(e) => table.setGlobalFilter(e.target.value)}
            />
            <button className="filter-button">
              <FunnelIcon className="icon-filter" />
            </button>
          </div>
          <button onClick={() => {}} className="add-person-button">
            Add Member
          </button>
        </div>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th className="table-header">PHOTO</th>
            <th className="table-header">NAME</th>
            <th className="table-header">EMAIL</th>
            <th className="table-header">ROLE</th>
            <th className="table-header">TEAM</th>
            <th className="table-header">ACTIONS</th>
          </tr>
        </thead>
        <tbody>
          {table.getRowModel().rows.map((row) => (
            <tr key={row.id} className="table-row">
              {row.getVisibleCells().map((cell) => (
                <td key={cell.id} className="table-cell">
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      {selectedMember && (
        <div className="personal-details-popup">
          <div className="personal-details-header">
            <div className="personal-details-photo">
              <img src={selectedMember.photo} alt={selectedMember.name} />
            </div>
            <div className="personal-details-info">
              <h2 className="personal-details-name">{selectedMember.name}</h2>
              <p className="personal-details-role">{selectedMember.role}</p>
              <p className="personal-details-username">@{selectedMember.name.toLowerCase().split(' ').join('')}</p>
            </div>
            <button className="close-popup-button" onClick={closePopup}>
              &times;
            </button>
          </div>
          <div className="personal-details-body">
            <h3>Personal Information</h3>
            <p>Date of Birth: {selectedMember.dob}</p>
            <p>Gender: {selectedMember.gender}</p>
            <p>Nationality: {selectedMember.nationality}</p>
            <p>Contact no.: {selectedMember.contact}</p>
            <p>Email Address: {selectedMember.email}</p>
            <p>Work Email Address: {selectedMember.workEmail}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default PeopleDirectory;
