import React, { useMemo, useState, useCallback } from 'react';
import { useReactTable, getCoreRowModel, getFilteredRowModel, getPaginationRowModel, getSortedRowModel, flexRender } from '@tanstack/react-table';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';

// Placeholder image URL
const placeholderImage = 'https://example.com/placeholder.jpg';

const dataSchema = z.object({
  name: z.string().min(1, 'Name is required'),
  age: z.number().min(1, 'Age is required'),
  photo: z.string().optional(),
  email: z.string().email('Invalid email address'),
  // Add more fields as needed
});

const defaultData = [
  { id: 1, name: 'John Doe', age: 28, role: 'Frontend Developer', team: 'Design', photo: placeholderImage, email: 'johndoe@example.com' },
  { id: 2, name: 'Jane Smith', age: 34, role: 'Backend Developer', team: 'Product', photo: placeholderImage, email: 'janesmith@example.com' },
  // Add more sample data as needed
];

function PeopleDirectory() {
  const [data, setData] = useState(defaultData);

  const handleDelete = useCallback((id) => {
    setData((prevData) => prevData.filter(item => item.id !== id));
  }, []);

  const columns = useMemo(
    () => [
      {
        accessorKey: 'photo',
        header: 'Photo',
        cell: ({ row }) => (
          <img src={row.original.photo} alt={row.original.name} className="w-10 h-10 rounded-full object-cover" />
        ),
      },
      {
        accessorKey: 'name',
        header: 'Name',
      },
      {
        accessorKey: 'email',
        header: 'Email',
      },
      {
        accessorKey: 'role',
        header: 'Role',
      },
      {
        accessorKey: 'team',
        header: 'Team',
      },
      {
        id: 'actions',
        header: 'Actions',
        cell: ({ row }) => (
          <div className="flex space-x-2">
            <button
              className="text-blue-500 hover:text-blue-700 rounded-md px-2 py-1"
              onClick={() => handleEdit(row.original)}
            >
              Edit
            </button>
            <button
              className="text-red-500 hover:text-red-700 rounded-md px-2 py-1"
              onClick={() => handleDelete(row.original.id)}
            >
              Delete
            </button>
          </div>
        ),
      },
    ],
    [handleDelete]
  );

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: zodResolver(dataSchema),
  });

  const handleEdit = (row) => {
    // Implement logic to open a form for editing the selected row
  };

  const onSubmit = (newData) => {
    setData((prevData) => [...prevData, { id: prevData.length + 1, ...newData, photo: placeholderImage }]);
  };

  return (
    <div className="p-6 bg-white rounded-md shadow-md max-w-7xl mx-auto">
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search..."
          className="border rounded-md p-2 w-full"
          onChange={(e) => table.setGlobalFilter(e.target.value)}
        />
      </div>
      <form onSubmit={handleSubmit(onSubmit)} className="mb-6 p-4 bg-gray-100 rounded-md space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700">Name</label>
            <input
              id="name"
              className="border rounded-md p-2 w-full"
              {...register('name')}
            />
            {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name.message}</p>}
          </div>
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email</label>
            <input
              id="email"
              type="email"
              className="border rounded-md p-2 w-full"
              {...register('email')}
            />
            {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email.message}</p>}
          </div>
          <div>
            <label htmlFor="age" className="block text-sm font-medium text-gray-700">Age</label>
            <input
              id="age"
              type="number"
              className="border rounded-md p-2 w-full"
              {...register('age')}
            />
            {errors.age && <p className="text-red-500 text-sm mt-1">{errors.age.message}</p>}
          </div>
          {/* Add more fields if necessary */}
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md">Add Person</button>
      </form>
      <table className="min-w-full table-auto border-collapse">
        <thead>
          {table.getHeaderGroups().map(headerGroup => (
            <tr key={headerGroup.id}>
              {headerGroup.headers?.map(header => (
                <th
                  key={header.id}
                  className="p-2 border text-gray-600 bg-gray-200 text-left font-medium text-sm uppercase tracking-wider"
                >
                  {flexRender(header.column.columnDef.header, header.getContext())}
                  {header.column.getIsSorted() ? (header.column.getIsSortedDesc() ? ' ðŸ”½' : ' ðŸ”¼') : ''}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map(row => (
            <tr key={row.id} className="even:bg-gray-50">
              {row.getVisibleCells().map(cell => (
                <td key={cell.id} className="p-2 border text-gray-900 text-sm">
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div className="mt-4 flex justify-end">
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded-md"
          onClick={() => table.previousPage()}
          disabled={!table.getCanPreviousPage()}
        >
          Previous
        </button>
        <button
          className="ml-2 bg-blue-500 text-white px-4 py-2 rounded-md"
          onClick={() => table.nextPage()}
          disabled={!table.getCanNextPage()}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default PeopleDirectory;
