import React, { useState } from "react";
import { useTable, useSortBy, useFilters, useGlobalFilter } from "@tanstack/react-table";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import 'tailwindcss/tailwind.css';

const dataSchema = z.object({
  name: z.string().min(1, "Name is required"),
  age: z.number().min(1, "Age is required"),
  // add more fields as needed
});

const defaultData = [
  { id: 1, name: "John Doe", age: 28 },
  { id: 2, name: "Jane Smith", age: 34 },
  // more default rows
];

function PeopleDirectory() {
  const [data, setData] = useState(defaultData);

  const columns = React.useMemo(
    () => [
      {
        Header: "ID",
        accessor: "id",
      },
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "Age",
        accessor: "age",
      },
      {
        Header: "Actions",
        Cell: ({ row }) => (
          <div className="flex space-x-2">
            <button
              className="text-blue-500"
              onClick={() => handleEdit(row)}
            >
              Edit
            </button>
            <button
              className="text-red-500"
              onClick={() => handleDelete(row.id)}
            >
              Delete
            </button>
          </div>
        ),
      },
    ],
    []
  );

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: zodResolver(dataSchema),
  });

  const tableInstance = useTable({ columns, data }, useFilters, useGlobalFilter, useSortBy);

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    setGlobalFilter,
  } = tableInstance;

  const handleEdit = (row) => {
    // handle edit logic
  };

  const handleDelete = (id) => {
    setData(data.filter(item => item.id !== id));
  };

  const onSubmit = (newData) => {
    setData([...data, { id: data.length + 1, ...newData }]);
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search..."
          className="border rounded p-2"
          onChange={(e) => setGlobalFilter(e.target.value)}
        />
      </div>
      <form onSubmit={handleSubmit(onSubmit)} className="mb-4 space-y-4">
        <input
          {...register("name")}
          placeholder="Name"
          className="border rounded p-2"
        />
        {errors.name && <p className="text-red-500">{errors.name.message}</p>}

        <input
          {...register("age")}
          type="number"
          placeholder="Age"
          className="border rounded p-2"
        />
        {errors.age && <p className="text-red-500">{errors.age.message}</p>}

        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          Add Person
        </button>
      </form>
      <table {...getTableProps()} className="min-w-full table-auto border-collapse">
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th
                  {...column.getHeaderProps(column.getSortByToggleProps())}
                  className="p-2 border-b-2 border-gray-300 bg-gray-100 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider"
                >
                  {column.render("Header")}
                  <span>
                    {column.isSorted
                      ? column.isSortedDesc
                        ? " ðŸ”½"
                        : " ðŸ”¼"
                      : ""}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map(row => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => (
                  <td
                    {...cell.getCellProps()}
                    className="p-2 border-b border-gray-200 text-sm"
                  >
                    {cell.render("Cell")}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default PeopleDirectory;
