import React, { useMemo, useState, useCallback } from 'react';
import { useReactTable, getCoreRowModel, getFilteredRowModel, getPaginationRowModel, getSortedRowModel, flexRender } from '@tanstack/react-table';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';

const dataSchema = z.object({
  name: z.string().min(1, 'Name is required'),
  age: z.number().min(1, 'Age is required'),
  // Add more fields as needed
});

const defaultData = [
  { id: 1, name: 'John Doe', age: 28, role: 'Frontend Developer', team: 'Design' },
  { id: 2, name: 'Jane Smith', age: 34, role: 'Backend Developer', team: 'Product' },
  // Add more sample data as needed
];

function PeopleDirectory() {
  const [data, setData] = useState(defaultData);

  const handleDelete = useCallback((id) => {
    setData((prevData) => prevData.filter(item => item.id !== id));
  }, []);

  const columns = useMemo(
    () => [
      {
        accessorKey: 'id',
        header: 'ID',
      },
      {
        accessorKey: 'name',
        header: 'Name',
      },
      {
        accessorKey: 'role',
        header: 'Role',
      },
      {
        accessorKey: 'team',
        header: 'Team',
      },
      {
        id: 'actions',
        header: 'Actions',
        cell: ({ row }) => (
          <div className="flex space-x-2">
            <button
              className="text-blue-500"
              onClick={() => handleEdit(row.original)}
            >
              Edit
            </button>
            <button
              className="text-red-500"
              onClick={() => handleDelete(row.original.id)}
            >
              Delete
            </button>
          </div>
        ),
      },
    ],
    [handleDelete]
  );

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: zodResolver(dataSchema),
  });

  const handleEdit = (row) => {
    // Implement logic to open a form for editing the selected row
  };

  const onSubmit = (newData) => {
    setData((prevData) => [...prevData, { id: prevData.length + 1, ...newData }]);
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search..."
          className="border rounded p-2"
          onChange={(e) => table.dispatch({ type: 'setGlobalFilter', filterValue: e.target.value })}
        />
      </div>
      <form onSubmit={handleSubmit(onSubmit)} className="mb-4 space-y-4">
        <div>
          <label htmlFor="name">Name</label>
          <input
            id="name"
            className="border p-2"
            {...register('name')}
          />
          {errors.name && <p>{errors.name.message}</p>}
        </div>
        <div>
          <label htmlFor="age">Age</label>
          <input
            id="age"
            type="number"
            className="border p-2"
            {...register('age')}
          />
          {errors.age && <p>{errors.age.message}</p>}
        </div>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add Person</button>
      </form>
      <table className="min-w-full table-auto border-collapse">
        <thead>
          {table.getHeaderGroups().map(headerGroup => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map(column => (
                <th
                  key={column.id}
                  className="p-2 border-b-2 border-gray-300 bg-gray-100 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider"
                >
                  {flexRender(column.columnDef.header, column.getContext())}
                  {column.getIsSorted() ? (column.getIsSortedDesc() ? ' ðŸ”½' : ' ðŸ”¼') : ''}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map(row => (
            <tr key={row.id}>
              {row.getVisibleCells().map(cell => (
                <td
                  key={cell.id}
                  className="p-2 border-b border-gray-300"
                >
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PeopleDirectory;
