import React, { useState } from 'react';
import { useTable, useSortBy, useFilters, usePagination } from 'react-table';
import { FaEdit, FaTrash } from 'react-icons/fa';

const PeopleDirectory = ({ data }) => {
  const [selectedPerson, setSelectedPerson] = useState(null);

  const handleEdit = (id) => {
    // Find the person by id
    const personToEdit = data.find(person => person.id === id);
    // Set the selected person for editing
    setSelectedPerson(personToEdit);
    // Optionally, navigate to an edit page or open a modal
    console.log(`Editing person with id: ${id}`);
  };

  const handleDelete = (id) => {
    // Logic to handle delete action
    console.log(`Deleting person with id: ${id}`);
    // You can update the state or make an API call to delete the person
  };

  const columns = React.useMemo(
    () => [
      {
        Header: 'Name',
        accessor: 'name',
      },
      {
        Header: 'Email',
        accessor: 'email',
      },
      {
        Header: 'Age',
        accessor: 'age',
      },
      {
        Header: 'Actions',
        Cell: ({ row }) => (
          <div className="flex space-x-2">
            <button onClick={() => handleEdit(row.original.id)} className="icon-edit">
              <FaEdit />
            </button>
            <button onClick={() => handleDelete(row.original.id)} className="icon-delete">
              <FaTrash />
            </button>
          </div>
        ),
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    page,
    canPreviousPage,
    canNextPage,
    pageOptions,
    pageCount,
    gotoPage,
    nextPage,
    previousPage,
    setPageSize,
    state: { pageIndex, pageSize },
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy,
    usePagination
  );

  return (
    <div className="people-directory">
      <div className="header">
        <h2 className="text-xl font-bold">People Directory</h2>
        <button
          onClick={() => console.log('Navigating to add person page')}
          className="add-person-button"
        >
          Add Person
        </button>
      </div>

      <table {...getTableProps()} className="table">
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())} className="table-header">
                  {column.render('Header')}
                  <span>
                    {column.isSorted
                      ? column.isSortedDesc
                        ? ' ðŸ”½'
                        : ' ðŸ”¼'
                      : ''}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map(row => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => {
                  return (
                    <td {...cell.getCellProps()} className="table-cell">
                      {cell.render('Cell')}
                    </td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* Pagination Controls */}
      <div className="pagination-container">
        <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
          {'<<'}
        </button>
        <button onClick={() => previousPage()} disabled={!canPreviousPage}>
          {'<'}
        </button>
        <span>
          Page{' '}
          <strong>
            {pageIndex + 1} of {pageOptions.length}
          </strong>{' '}
        </span>
        <button onClick={() => nextPage()} disabled={!canNextPage}>
          {'>'}
        </button>
        <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
          {'>>'}
        </button>
        <select
          value={pageSize}
          onChange={e => setPageSize(Number(e.target.value))}
        >
          {[10, 20, 30, 40, 50].map(pageSize => (
            <option key={pageSize} value={pageSize}>
              Show {pageSize}
            </option>
          ))}
        </select>
      </div>

      {/* Personal Details Modal (if needed) */}
      {selectedPerson && (
        <div className="personal-details-modal">
          <div className="personal-details-content">
            <button className="close-button" onClick={() => setSelectedPerson(null)}>
              &times;
            </button>
            <div className="personal-details-header">
              <img
                src={selectedPerson.photo}
                alt={`${selectedPerson.name}'s profile`}
                className="member-photo-large"
              />
              <div className="member-info">
                <h3>{selectedPerson.name}</h3>
                <p>{selectedPerson.email}</p>
              </div>
            </div>
            <div className="personal-details-body">
              <p><strong>Age:</strong> {selectedPerson.age}</p>
              {/* Add more personal details here */}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PeopleDirectory;
