// src/components/PeopleDirectory.jsx

import React, { useMemo, useState } from "react";
import { useReactTable, getCoreRowModel, getSortedRowModel, getFilteredRowModel } from "@tanstack/react-table";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import 'tailwindcss/tailwind.css';

const dataSchema = z.object({
  name: z.string().min(1, "Name is required"),
  age: z.number().min(1, "Age is required"),
  // add more fields as needed
});

const defaultData = [
  { id: 1, name: "John Doe", age: 28 },
  { id: 2, name: "Jane Smith", age: 34 },
  // more default rows
];

function PeopleDirectory() {
  const [data, setData] = useState(defaultData);

  const columns = useMemo(
    () => [
      {
        accessorKey: "id",
        header: "ID",
      },
      {
        accessorKey: "name",
        header: "Name",
      },
      {
        accessorKey: "age",
        header: "Age",
      },
      {
        header: "Actions",
        cell: ({ row }) => (
          <div className="flex space-x-2">
            <button
              className="text-blue-500"
              onClick={() => handleEdit(row)}
            >
              Edit
            </button>
            <button
              className="text-red-500"
              onClick={() => handleDelete(row.original.id)}
            >
              Delete
            </button>
          </div>
        ),
      },
    ],
    []
  );

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: zodResolver(dataSchema),
  });

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
  });

  const handleEdit = (row) => {
    // handle edit logic
  };

  const handleDelete = (id) => {
    setData(data.filter(item => item.id !== id));
  };

  const onSubmit = (newData) => {
    setData([...data, { id: data.length + 1, ...newData }]);
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search..."
          className="border rounded p-2"
          onChange={(e) => table.setGlobalFilter(e.target.value)}
        />
      </div>
      <form onSubmit={handleSubmit(onSubmit)} className="mb-4 space-y-4">
        <input
          {...register("name")}
          placeholder="Name"
          className="border rounded p-2"
        />
        {errors.name && <p className="text-red-500">{errors.name.message}</p>}

        <input
          {...register("age")}
          type="number"
          placeholder="Age"
          className="border rounded p-2"
        />
        {errors.age && <p className="text-red-500">{errors.age.message}</p>}

        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          Add Person
        </button>
      </form>
      <table className="min-w-full table-auto border-collapse">
        <thead>
          {table.getHeaderGroups().map(headerGroup => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map(header => (
                <th
                  key={header.id}
                  className="p-2 border-b-2 border-gray-300 bg-gray-100 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider"
                  onClick={header.column.getToggleSortingHandler()}
                >
                  {header.isPlaceholder ? null : (
                    <div>
                      {header.render("Header")}
                      <span>
                        {header.column.getIsSorted()
                          ? header.column.getIsSorted() === "desc"
                            ? " ðŸ”½"
                            : " ðŸ”¼"
                          : null}
                      </span>
                    </div>
                  )}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map(row => (
            <tr key={row.id}>
              {row.getVisibleCells().map(cell => (
                <td
                  key={cell.id}
                  className="p-2 border-b border-gray-200 text-sm"
                >
                  {cell.render("Cell")}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PeopleDirectory;
