import React from 'react';
// import './PersonalDetails.css'; // Add custom styles here for your modal

function PersonalDetails({ member, onClose }) {
  if (!member) return null;

  return (
    <div className="personal-details-modal">
      <div className="personal-details-content">
        <button onClick={onClose} className="close-button">&times;</button>
        <div className="personal-details-header">
          <img src={member.photo} alt={`${member.name}`} className="member-photo-large" />
          <div className="member-info">
            <h2>{member.name}</h2>
            <p>{member.role}</p>
            <p>@{member.name.toLowerCase().replace(' ', '')}</p>
          </div>
        </div>
        <div className="personal-details-body">
          <h3>Personal Information</h3>
          <p><strong>Date of Birth:</strong> 29-04-2005</p>
          <p><strong>Gender:</strong> Female</p>
          <p><strong>Nationality:</strong> Canadian</p>
          <p><strong>Contact no.:</strong> 1234567890</p>
          <p><strong>Email:</strong> {member.email}</p>
          <p><strong>Work email Address:</strong> {member.email}</p>
        </div>
      </div>
    </div>
  );
}

export default PersonalDetails;
