  };

  return (
    <div className="container mx-auto px-4 py-8">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">PEOPLE.CO</h1>
        <div className="search-bar flex items-center border rounded-md p-2 w-1/3">
          <input
            type="text"
            placeholder="Search..."
            className="flex-grow px-2"
            onChange={(e) => table.setGlobalFilter(e.target.value)}
          />
          <button className="bg-blue-500 text-white px-3 py-2 rounded-md">Search</button>
        </div>
      </header>
      <div className="flex">
        <aside className="w-1/4 bg-gray-100 p-4">
          <h2 className="text-lg font-medium mb-2">Overview</h2>
          <h2 className="text-lg font-medium mb-2">People Directory</h2>
        </aside>
        <main className="flex-grow">
          <h2 className="text-2xl font-bold mb-4">Team Members (100 users)</h2>
          <table className="min-w-full table-auto border-collapse">
            <thead>
              {table.getHeaderGroups().map(headerGroup => (
                <tr key={headerGroup.id}>
                  {headerGroup.headers?.map(header => (
                    <th
                      key={header.id}
                      className="p-2 border text-gray-700 bg-gray-200 text-left font-medium text-xs uppercase tracking-wider"
                    >
                      {flexRender(header.column.columnDef.header, header.getContext())}
                      {header.column.getIsSorted() ? (header.column.getIsSortedDesc() ? ' ðŸ”½' : ' ðŸ”¼') : ''}
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody>
              {table.getRowModel().rows.map(row => (
                <tr key={row.id}>
                  {row.getVisibleCells().map(cell => (
                    <td key={cell.id} className="p-2 border text-gray-900 text-sm">
                      {flexRender(cell.column.columnDef.cell, cell.getContext())}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </main>
      </div>
    </div>
  );
}

export default PeopleDirectory;
