import React, { useMemo, useState, useCallback } from 'react';
import { useReactTable, getCoreRowModel, getFilteredRowModel, getPaginationRowModel, getSortedRowModel, flexRender } from '@tanstack/react-table';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';

// Placeholder image URL
const placeholderImage = 'https://example.com/placeholder.jpg';

const dataSchema = z.object({
  name: z.string().min(1, 'Name is required'),
  age: z.number().min(1, 'Age is required'),
  photo: z.string().optional(),
  email: z.string().email('Invalid email address'),
  // Add more fields as needed
});

const defaultData = [
  { id: 1, name: 'John Doe', age: 28, role: 'Frontend Developer', team: 'Design', photo: placeholderImage, email: 'johndoe@example.com' },
  { id: 2, name: 'Jane Smith', age: 34, role: 'Backend Developer', team: 'Product', photo: placeholderImage, email: 'janesmith@example.com' },
  // Add more sample data as needed
];

function PeopleDirectory() {
  const [data, setData] = useState(defaultData);
  const [editRow, setEditRow] = useState(null);

  const handleDelete = useCallback((id) => {
    setData((prevData) => prevData.filter(item => item.id !== id));
  }, []);

  const columns = useMemo(
    () => [
      {
        accessorKey: 'id',
        header: 'ID',
      },
      {
        accessorKey: 'photo',
        header: 'Photo',
        cell: ({ row }) => (
          <img src={row.original.photo || placeholderImage} alt={row.original.name} className="w-12 h-12 rounded-full" />
        ),
      },
      {
        accessorKey: 'name',
        header: 'Name',
      },
      {
        accessorKey: 'email',
        header: 'Email',
      },
      {
        accessorKey: 'role',
        header: 'Role',
      },
      {
        accessorKey: 'team',
        header: 'Team',
      },
      {
        id: 'actions',
        header: 'Actions',
        cell: ({ row }) => (
          <div className="flex space-x-2">
            <button
              className="text-blue-500 hover:bg-blue-600 hover:text-white rounded-md px-2 py-1"
              onClick={() => handleEdit(row.original)}
            >
              Edit
            </button>
            <button
              className="text-red-500 hover:bg-red-600 hover:text-white rounded-md px-2 py-1"
              onClick={() => handleDelete(row.original.id)}
            >
              Delete
            </button>
          </div>
        ),
      },
    ],
    [handleDelete]
  );

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    reset,
  } = useForm({
    resolver: zodResolver(dataSchema),
  });

  const handleEdit = (row) => {
    setEditRow(row);
    setValue('name', row.name);
    setValue('email', row.email);
    setValue('age', row.age);
    setValue('photo', row.photo);
  };

  const onSubmit = (newData) => {
    if (editRow) {
      setData((prevData) =>
        prevData.map((item) =>
          item.id === editRow.id ? { ...item, ...newData } : item
        )
      );
      setEditRow(null);
    } else {
      setData((prevData) => [...prevData, { id: prevData.length + 1, ...newData }]);
    }
    reset();
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search..."
          className="border rounded-md p-2 w-full"
          onChange={(e) => table.setGlobalFilter(e.target.value)}
        />
      </div>
      <form onSubmit={handleSubmit(onSubmit)} className="mb-4 space-y-4">
        <div>
          <label htmlFor="name">Name</label>
          <input
            id="name"
            className="border rounded-md p-2 w-full"
            {...register('name')}
          />
          {errors.name && <p className="text-red-500">{errors.name.message}</p>}
        </div>
        <div>
          <label htmlFor="email">Email</label>
          <input
            id="email"
            type="email"
            className="border rounded-md p-2 w-full"
            {...register('email')}
          />
          {errors.email && <p className="text-red-500">{errors.email.message}</p>}
        </div>
        <div>
          <label htmlFor="age">Age</label>
          <input
            id="age"
            type="number"
            className="border rounded-md p-2 w-full"
            {...register('age')}
          />
          {errors.age && <p className="text-red-500">{errors.age.message}</p>}
        </div>
        <div>
          <label htmlFor="photo">Photo URL</label>
          <input
            id="photo"
            type="text"
            className="border rounded-md p-2 w-full"
            {...register('photo')}
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md">
          {editRow ? 'Update Person' : 'Add Person'}
        </button>
        {editRow && (
          <button
            type="button"
            onClick={() => {
              setEditRow(null);
              reset();
            }}
            className="ml-2 bg-gray-500 text-white px-4 py-2 rounded-md"
          >
            Cancel Edit
          </button>
        )}
      </form>
      <table className="min-w-full table-auto border-collapse">
        <thead>
          {table.getHeaderGroups().map(headerGroup => (
            <tr key={headerGroup.id}>
              {headerGroup.headers?.map(header => (
                <th
                  key={header.id}
                  className="p-2 border text-gray-600 bg-gray-200 text-left font-medium text-xs uppercase tracking-wider"
                >
                  {flexRender(header.column.columnDef.header, header.getContext())}
                  {header.column.getIsSorted() ? (header.column.getIsSortedDesc() ? ' ðŸ”½' : ' ðŸ”¼') : ''}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map(row => (
            <tr key={row.id}>
              {row.getVisibleCells().map(cell => (
                <td key={cell.id} className="p-2 border text-gray-900 text-sm">
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PeopleDirectory;
