import React, { useMemo, useState, useCallback } from 'react';
import { useReactTable, getCoreRowModel, getFilteredRowModel, getPaginationRowModel, getSortedRowModel, flexRender } from '@tanstack/react-table';
import '../styles.css';

// Assuming you have a placeholder image URL
const placeholderImage = 'https://example.com/placeholder.jpg';

const defaultData = [
  {  name: 'John Doe', age: 28, role: 'Frontend Developer', team: 'Design', photo: placeholderImage, email: 'johndoe@example.com' },
  {  name: 'Jane Smith', age: 34, role: 'Backend Developer', team: 'Product', photo: placeholderImage, email: 'janesmith@example.com' },
  // Add more sample data as needed
];

function PeopleDirectory() {
  const [data, setData] = useState(defaultData);

  const handleDelete = useCallback((id) => {
    setData((prevData) => prevData.filter(item => item.id !== id));
  }, []);

  const columns = useMemo(
    () => [
      // {
      //   accessorKey: 'id',
      //   header: 'ID',
      // },
      {
        accessorKey: 'photo',
        header: 'Photo',
        cell: ({ row }) => (
          <img src={row.original.photo}  className="member-photo" />
        ),
      },
      {
        accessorKey: 'name',
        header: 'Name',
        cell: ({ row }) => (
          <span className="member-name">{row.original.name}</span>
        ),
      },
      {
        accessorKey: 'email',
        header: 'Email',
        cell: ({ row }) => (
          <span className="member-info">{row.original.email}</span>
        ),
      },
      {
        accessorKey: 'role',
        header: 'Role',
        cell: ({ row }) => (
          <span className="member-info">{row.original.role}</span>
        ),
      },
      {
        accessorKey: 'team',
        header: 'Team',
        cell: ({ row }) => (
          <span className="member-status">{row.original.team}</span>
        ),
      },
      {
        id: 'actions',
        header: 'Actions',
        cell: ({ row }) => (
          <div className="member-actions">
            <button
              className="member-action-button"
              onClick={() => handleEdit(row.original)}
            >
              Edit
            </button>
            <button
              className="member-action-button text-red-500 hover:bg-red-600 hover:text-white"
              onClick={() => handleDelete(row.original.id)}
            >
              Delete
            </button>
          </div>
        ),
      },
    ],
    [handleDelete]
  );

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
  });

  const handleEdit = (row) => {
    // Implement logic to open a form for editing the selected row
  };

  const handleAddPerson = () => {
    // Navigate to the add person page
    // history.push('/add-person'); // or navigate('/add-person') for react-router-dom v6+
  };

  return (
      <div className="people-directory">
        <div className="header">
          <input
            type="text"
            placeholder="Search..."
            className="search-bar search-input"
            onChange={(e) => table.setGlobalFilter(e.target.value)}
          />
        </div>
        <button
          onClick={handleAddPerson}
          className="add-person-button pl"
        >
          Add Person
        </button>
        <table className="table">
          <thead>
            <tr>
              <th className="table-header">PHOTO</th>
              <th className="table-header">NAME</th>
              <th className="table-header">EMAIL</th>
              <th className="table-header">ROLE</th>
              <th className="table-header">TEAM</th>
              <th className="table-header">ACTIONS</th>
            </tr>
          </thead>
          <tbody>
            {table.getRowModel().rows.map(row => (
              <tr key={row.id} className="table-row">
                {row.getVisibleCells().map(cell => (
                  <td key={cell.id} className="table-cell">
                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }

export default PeopleDirectory;
